import React from 'react'
import { useState, useEffect } from 'react'

export default function Main() {

    const [todo, setTodo] = useState('')
    const [todos, setTodos] = useState([])

    const handleChange = e => {
        setTodo (e.target.value)
    }

    const handleSubmit = e => {
        e.preventDefault()
        if(todo !== '') {
            setTodos([...todos, todo])
            setTodo('')
        }

    }

    const deleteTodo = (text) => {
        const newTodos = todos.filter((todo) => {
            return todo !== text;
        } )
        console.log(newTodos)
        setTodos(newTodos)
    }

    const completeTodo = e => {
        const currentTodo = e.target.parentElement.parentElement.parentElement.firstChild
        currentTodo.classList.toggle("completed")
    }

    useEffect(() => {
        console.log("todo", todos)
    }, [todos])

  return (
    <main>
        <form onSubmit={handleSubmit} className="todo-form" >
            <input type="text" placeholder="Add a todo" value={todo} onChange={handleChange} name="text" className="todo-input" />
            <button className="todo-button">Add todo</button>
        </form>
        {todos.length > 0 ? (
            <ul> 

                    {todos.map((todo, index) => (
                        <div class = "todo-container">
                            <li key={index}>{todo}</li>
                            <div>
                                <button class="complete-btn" onClick={completeTodo}>
                                <i class="fas fa-check">Complete</i>
                                </button>
                                <button class="trash-btn" onClick={() => deleteTodo(todo)}>
                                    <i class="fas fa-trash">Delete</i>
                                </button>
                            </div>
                        </div>
                    ))}
            </ul>
        ) : <ul>
            <li>No todos</li>

            </ul>}
    </main>
  )
}
