import React from "react";

export default function TodoList({
  todos,
  deleteTodo,
  completeTodo,
}) {

    const handleChange = e => {
        const currbtnChild = e.target.firstChild
        currbtnChild.classList = "fa-solid"
        currbtnChild.classList.add("fa-xmark")
    }
  return todos.length > 0 ? (
    <ul>
      {todos.map((todo, index) => (
        <div class="todo-container">
          <li key={index}>{todo}</li>
          <div>
            <button class="complete-btn" onClick={completeTodo}>
                <i class="fa-solid fa-check" onChange={handleChange}></i>
            </button>
            <button class="trash-btn" onClick={() => deleteTodo(index)}>
                <i class="fa-solid fa-trash"></i>
            </button>
          </div>
        </div>
      ))}
    </ul>
  ) : (
    <ul>
      <li>No todos</li>
    </ul>
  );
}
