import React from 'react'
import { useState, useEffect } from 'react'
import TodoForm from './TodoForm'
import TodoList from './TodoList'

export default function Main() {

    const [todo, setTodo] = useState('')
    const [todos, setTodos] = useState([])

    const handleChange = e => {
        setTodo (e.target.value)
    }

    const handleSubmit = e => {
        e.preventDefault()
        if(todo !== '') {
            setTodos([...todos, todo])
            setTodo('')
        }

    }

    const deleteTodo = (index) => {
        const newTodos = todos.filter((todo, i) => i !== index)

        setTodos(newTodos)
    }

    const completeTodo = e => {
        const currentbtn = e.target
        const currbtnChild = currentbtn.firstChild
        currentbtn.classList.toggle("fa-solid fa-xmark")
        const currentTodo = e.target.parentElement.parentElement.firstChild
        currentTodo.classList.toggle("completed")

    }

    useEffect(() => {
        console.log("todo", todos)
    }, [todos])

  return (
    <main>
        <TodoForm todo={todo} setTodo={setTodo} handleChange={handleChange} handleSubmit={handleSubmit} />
        <TodoList todos={todos} deleteTodo={deleteTodo} completeTodo={completeTodo} />
    </main>
  )
}
