import React from "react";

export default function TodoList({
  todos,
  deleteTodo,
  completeTodo,
}) {

    const parent = document.querySelector('.todo-container')
    const child = parent.querySelector('li')

  return todos.length > 0 ? (
    <ul>
      {todos.map((todo, index) => (
        <div class="todo-container">
          <li key={index}>{todo}</li>
          <div>
            <button class="complete-btn" onClick={completeTodo}>
                { 
                    child.classList.contains('completed') ?
                    <i class="fa-solid fa-check"></i> :
                    <i class="fa-solid fa-xmark"></i>

                }
            </button>
            <button class="trash-btn" onClick={() => deleteTodo(index)}>
                <i class="fa-solid fa-trash"></i>
            </button>
          </div>
        </div>
      ))}
    </ul>
  ) : (
    <ul>
      <li>No todos</li>
    </ul>
  );
}
